SELECT '全部' as `day`, sum(sms1_count) AS sms1Count, sum(sms1_fee) AS sms1Fee, sum(sms2_count) AS sms2Count, sum(sms2_fee) AS sms2Fee, sum(youdun_count) AS youdunCount, sum(youdun_fee) AS youdunFee, sum(operator_count) AS operatorCount, sum(operator_fee) AS operatorFee, sum(risk_count) AS riskCount, sum(risk_fee) AS riskFee, sum(refused_count) AS refusedCount FROM tb_merchant_fee_statistics
