mapper.select:
select
a.id,a.uid,a.borrow_day,a.borrow_money,a.actual_money,a.overdue_day,a.overdue_fee,a.should_repay,a.reduce_money,a.status,a.user_type,
a.arrive_time,a.repay_time,a.real_repay_time,b.user_phone,b.user_name,c.user_name as
follow_name,a.real_repay_time,a.recycle_type
from tb_order a left join tb_user b on a.uid=b.id left join tb_merchant_manager c on a.follow_user_id=c.id


        and a.status=#{orderStatus}


        and a.follow_user_id=#{followUserId}


        and a.merchant=#{merchant}


        and a.overdue_day>=#{overdueDayDown}


        and a.overdue_day <=#{overdueDayUp}


        and a.repay_time>=#{repayTimeDown}


        and a.repay_time <=#{repayTimeUp}


        and a.user_type=#{userType}


        and b.user_phone=#{userPhone}


order by


        a.real_repay_time desc


        a.id desc


limit #{startIndex},#{pageSize}

select count(0) from tb_order a


        and a.status=#{orderStatus}


        and a.follow_user_id=#{followUserId}


        and a.merchant=#{merchant}


        and a.overdue_day>=#{overdueDayDown}


        and a.overdue_day <=#{overdueDayUp}


        and a.repay_time>=#{repayTimeDown}


        and a.repay_time <=#{repayTimeUp}


        and a.user_type=#{userType}


        and a.uid = (select id from tb_user u where u.user_phone=#{userPhone} and u.merchant = #{merchant})

select a.id,a.follow_status,a.type,a.remark,a.create_time, b.user_name as follow_name
from tb_order_recycle_record a left join tb_merchant_manager b on a.follow_user_id =b.id


        and a.merchant=#{merchant}


        and a.order_id=#{orderId}


        and a.uid=#{uid}


ORDER BY id desc

select a.uid,c.user_name,c.user_phone,a.id, b.user_name as
follow_name,a.follow_status,a.remark,a.create_time,a.type
from tb_order_recycle_record a left join tb_merchant_manager b on a.follow_user_id =b.id left join tb_user c on
a.uid=c.id


        and a.merchant=#{merchant}


        and c.user_phone=#{userPhone}


        and a.create_time >= #{createTimeUp}


        and a.create_time < date_add(#{createTimeDown},interval 1 day)


ORDER BY a.id desc limit #{startIndex},#{pageSize}

select count(*)
from tb_order_recycle_record a left join tb_merchant_manager b on a.follow_user_id =b.id left join tb_user c on
a.uid=c.id


        and a.merchant=#{merchant}


        and c.user_phone=#{userPhone}


        and a.create_time >= #{createTimeUp}


        and a.create_time < date_add(#{createTimeDown},interval 1 day)

		select count(distinct(o.uid))
		from tb_order o
		left join tb_user u on u.id = o.uid
		left join tb_user_info ui on ui.uid = o.uid
		left join tb_moxie_mobile mm on mm.uid = o.uid
		left join tb_user_address_list ual on ual.uid = o.uid
		left join tb_merchant m on m.merchant_alias = o.merchant


                and o.status=#{orderStatus}


                and o.follow_user_id=#{followUserId}


                and m.merchant_alias=#{merchant}


                and o.overdue_day>=#{overdueDayDown}


                and o.overdue_day <=#{overdueDayUp}


                and o.repay_time>=#{repayTimeDown}


                and o.repay_time <=#{repayTimeUp}


                and o.user_type=#{userType}


                and u.user_phone=#{userPhone}

SELECT IFNULL(t.audit_name, '风控系统') 'audit', count(0) 'order_apply_cnt', sum(t.is_pass) 'order_pass_cnt', sum(t.is_pass)/count(0) 'pass_rate', sum(case when t.dq_days>=0 and t.overdue_cur>0 then 1 else 0 end)/sum(case when t.dq_days>=0 then 1 else 0 end) 'current_overdue_rate', sum(case when t.dq_days>0 and t.overdue_day>0 then 1 else 0 end)/sum(case when t.dq_days>0 then 1 else 0 end) 'first_overdue_rate', sum(case when t.dq_days>0 and t.overdue_day>2 then 1 else 0 end)/sum(case when t.dq_days>2 then 1 else 0 end) 'overdue_3_rate', sum(case when t.dq_days>0 and t.overdue_day>3 then 1 else 0 end)/sum(case when t.dq_days>3 then 1 else 0 end) 'overdue_4_rate', sum(case when t.dq_days>0 and t.overdue_day>6 then 1 else 0 end)/sum(case when t.dq_days>6 then 1 else 0 end) 'overdue_7_rate', sum(case when t.dq_days>0 and t.overdue_day>14 then 1 else 0 end)/sum(case when t.dq_days>14 then 1 else 0 end) 'overdue_15_rate', sum(case when t.dq_days>0 and t.overdue_day>29 then 1 else 0 end)/sum(case when t.dq_days>29 then 1 else 0 end) 'overdue_30_rate' from (SELECT cr.create_time,repay_time,real_repay_time,tad.audit_name,cr.overdue_day, case when cr.status > 20 and cr.status <50 then 1 else 0 end is_pass, case when repay_time is not null then DATEDIFF(NOW(),repay_time) else null end as dq_days, case when cr.status in (33,34) then 1 else null end as overdue_cur from tb_order cr LEFT JOIN tb_order_audit tad on cr.id = tad.order_id
and cr.create_time >= #{startTime}
and cr.create_time <= DATE_ADD(#{endTime},INTERVAL 1 DAY)
and cr.merchant = #{merchant}
and cr.user_type = #{userType}
)t GROUP BY 1

SELECT mm.user_name as 'recycled_name', sum(case when follow_user_id<>0 then 1 else 0 end) 'recycle_cnt', sum(case when real_repay_time is null then 1 else 0 end)'not_return_cnt', sum(case when real_repay_time is null then 1 else 0 end)/count(0) 'current_rate', sum(case when overdue_day>0 and overdue_day<2 and follow_user_id<>0 and real_repay_time is not null then 1 else 0 end)/sum(case when overdue_day>0 then 1 else 0 end) 'repay_1_rate', sum(case when overdue_day>1 and overdue_day<4 and follow_user_id<>0 and real_repay_time is not null then 1 else 0 end)/sum(case when overdue_day>0 then 1 else 0 end) 'repay_3_rate', sum(case when overdue_day>3 and overdue_day<8 and follow_user_id<>0 and real_repay_time is not null then 1 else 0 end)/sum(case when overdue_day>0 then 1 else 0 end) 'repay_7_rate', sum(case when overdue_day>7 and overdue_day<61 and follow_user_id<>0 and real_repay_time is not null then 1 else 0 end)/sum(case when overdue_day>0 then 1 else 0 end) 'repay_60_rate', sum(case when follow_user_id != 0 and real_repay_time is not null then 1 else 0 end)/sum(case when overdue_day>0 then 1 else 0 end) 'sum_repay_rate' from tb_order cr left join tb_merchant_manager mm on cr.follow_user_id = mm.id
and cr.user_type = #{userType}
and cr.merchant = #{merchant}
and cr.repay_time >= #{startTime}
and cr.repay_time < DATE_ADD(#{endTime},INTERVAL 1 DAY)
and cr.follow_user_id != 0
GROUP BY 1
