mapper.select:
select count(1) from report_partner_effect_deduction

    and user_origin in (select CONCAT(origin_id, '') from tb_merchant_manager_origin where manager_id=#{managerId})


        and merchant = #{merchant}


        and user_origin = #{userOrigin}


        and day_key >= #{startTime}


        and day_key <= #{endTime}

select * from report_partner_effect_deduction pe left join tb_merchant_origin mo on pe.user_origin = mo.id

    and pe.user_origin in (select CONCAT(origin_id, '') from tb_merchant_manager_origin where manager_id=#{managerId})


        and pe.merchant = #{merchant}


        and user_origin = #{userOrigin}


        and pe.day_key >= #{startTime}


        and pe.day_key <= #{endTime}


order by pe.id desc limit #{startIndex},#{pageSize}

select pe.day_key,ifnull(mo.origin_name,pe.user_origin) as 'user_origin',pe.reg_cnt,pe.real_name_cnt,
pe.submit_order_cnt,pe.first_submit_cnt,pe.login_cnt,pe.first_submit_amount from report_partner_effect_deduction pe left
join tb_merchant_origin mo on pe.user_origin = mo.id

    and pe.user_origin in (select CONCAT(origin_id, '') from tb_merchant_manager_origin where manager_id=#{managerId})


        and pe.merchant = #{merchant}


        and pe.user_origin = #{userOrigin}


        and pe.day_key >= #{startTime}


        and pe.day_key <= #{endTime}


order by pe.id
