mapper.select:
select count(DISTINCT day_key) from report_partner_effect


        and merchant = #{merchant}


        and user_origin = #{userOrigin}


        and day_key >= #{startTime}


        and day_key <= #{endTime}

SELECT day_key as dayKey, sum(reg_cnt) as regCnt, sum(real_name_cnt) as realNameCnt, sum(personal_info_certi_cnt) as personalInfoCertiCnt, sum(yys_cnt) as yysCnt, sum(bank_cnt) as bankCnt, sum(order_cnt) as orderCnt, sum(pass_risk_cnt) as passRiskCnt, sum(loan_success_cnt) as loanSuccessCnt , concat(ROUND(sum(real_name_cnt)/sum(reg_cnt)*100,2),'%') AS realNameCertiRate, concat(ROUND(sum(personal_info_certi_cnt)/sum(reg_cnt)*100,2),'%') AS personalInfoCertiRate, concat(ROUND(sum(yys_cnt)/sum(reg_cnt)*100,2),'%') AS yysCertiRate, concat(ROUND(sum(bank_cnt)/sum(reg_cnt)*100,2),'%') AS bankBoundRate, concat(ROUND(sum(order_cnt)/sum(reg_cnt)*100,2),'%') AS regApplyTransRate, concat(ROUND(sum(loan_success_cnt)/sum(reg_cnt)*100,2),'%') AS loanRate, concat(ROUND(sum(loan_success_cnt)/sum(order_cnt)*100,2),'%') AS auditPassRate from report_partner_effect pe
and pe.merchant = #{merchant}
and user_origin = #{userOrigin}
and pe.day_key >= #{startTime}
and pe.day_key <= #{endTime}
group by 1 order by pe.day_key desc
limit #{startIndex},#{pageSize}

SELECT day_key as dayKey, sum(reg_cnt) as regCnt, sum(real_name_cnt) as realNameCnt, sum(personal_info_certi_cnt) as personalInfoCertiCnt, sum(yys_cnt) as yysCnt, sum(bank_cnt) as bankCnt, sum(order_cnt) as orderCnt, sum(pass_risk_cnt) as passRiskCnt, sum(loan_success_cnt) as loanSuccessCnt , concat(ROUND(sum(real_name_cnt)/sum(reg_cnt)*100,2),'%') AS realNameCertiRate, concat(ROUND(sum(personal_info_certi_cnt)/sum(reg_cnt)*100,2),'%') AS personalInfoCertiRate, concat(ROUND(sum(yys_cnt)/sum(reg_cnt)*100,2),'%') AS yysCertiRate, concat(ROUND(sum(bank_cnt)/sum(reg_cnt)*100,2),'%') AS bankBoundRate, concat(ROUND(sum(order_cnt)/sum(reg_cnt)*100,2),'%') AS regApplyTransRate, concat(ROUND(sum(loan_success_cnt)/sum(reg_cnt)*100,2),'%') AS loanRate, concat(ROUND(sum(loan_success_cnt)/sum(order_cnt)*100,2),'%') AS auditPassRate from report_partner_effect pe
and pe.merchant = #{merchant}
and user_origin = #{userOrigin}
and pe.day_key >= #{startTime}
and pe.day_key <= #{endTime}
group by 1 order by pe.day_key desc

select count(1) from report_partner_effect pe left
join tb_merchant_origin mo on pe.user_origin = mo.id


        and pe.merchant = #{merchant}


        and mo.origin_name = #{originName}


        and pe.day_key = #{date}

SELECT day_key as dayKey, user_origin, mo.origin_name as originName, reg_cnt as regCnt, real_name_cnt as realNameCnt, personal_info_certi_cnt as personalInfoCertiCnt, yys_cnt as yysCnt, bank_cnt as bankCnt, order_cnt as orderCnt, pass_risk_cnt as passRiskCnt, loan_success_cnt as loanSuccessCnt , concat(ROUND(real_name_cnt/reg_cnt*100,2),'%') AS realNameCertiRate, concat(ROUND(personal_info_certi_cnt/reg_cnt*100,2),'%') AS personalInfoCertiRate, concat(ROUND(yys_cnt/reg_cnt*100,2),'%') AS yysCertiRate, concat(ROUND(bank_cnt/reg_cnt*100,2),'%') AS bankBoundRate, concat(ROUND(order_cnt/reg_cnt*100,2),'%') AS regApplyTransRate, concat(ROUND(loan_success_cnt/reg_cnt*100,2),'%') AS loanRate, concat(ROUND(loan_success_cnt/order_cnt*100,2),'%') AS auditPassRate from report_partner_effect pe left join tb_merchant_origin mo on pe.user_origin = mo.id
and pe.merchant = #{merchant}
and mo.origin_name = #{originName}
and pe.day_key = #{date}
GROUP BY 1,2 ORDER BY 1 DESC limit #{startIndex},#{pageSize}
